import React, { useState } from 'react';
import Alert from 'react-bootstrap/Alert';

import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';

function AlertFailed(props) {
    const [show, setShow] = useState(props.alerts.show);

    console.log("Alert danger: isFailed: "+props.alerts.show+" Field: "+props.alerts.field);
    console.log(show);

    if (show) {
        return (
            <Alert variant="danger" onClose={() => setShow(false)} dismissible>
                <Alert.Heading>Something goes wrong!</Alert.Heading>
                <p>
                    The {props.field} is not correct.
          </p>
            </Alert>
        );
    }
    return (
        <></>
    );
}

export default AlertFailed;
