import React, { useState, useEffect } from 'react';
import getInstance from './EthereumConnexion';

function CheckBalnce(props) {

    const [balance, setBalance] = useState();

    console.log("Checking balance of account: " + props.account);

    useEffect(() => {

        if (props.account != undefined) {
            const tokenInstance = getInstance();

            tokenInstance.balanceOf(props.account).then(function (result) {
                console.log(result);
                setBalance(result);
            }).catch(function (error) {
                console.log(error);
            })
        }
    }, []);

    return (
        <>
            <p>My balance: {balance} IKC</p>
        </>
    );
};

export default CheckBalnce;
