import React, { useState, useEffect } from 'react';
import { getInstance, getAccounts} from './EthereumConnexion';

function IkaCoin() {

    const [name, setName] = useState("");
    const [symbol, setSymbol] = useState("");
    const [totalSupply, setTotalSupply] = useState(0);
    const [accounts, setAccounts] = useState([]);

    useEffect(() => {

        setAccounts(getAccounts());

        const tokenInstance = getInstance();

        tokenInstance.name().then(function (result) {
            setName(result);
        }).catch(function (error) {
            console.log(error);
        });

        tokenInstance.symbol().then(function(result){
            setSymbol(result);
        }).catch(function(error){
            console.log(error);
        });

        tokenInstance.totalSupply().then(function(result){
            let supp = parseFloat(result.toString()) / 10 ** 18;
            setTotalSupply(supp);
        }).catch(function(error){
            console.log(error);
        });

    }, []);

    return (
        <>
            <p><b>{name}</b></p>
            <p>Total Supply: {totalSupply} {symbol}</p>
            <p>My balance: {totalSupply} {symbol}</p>
            <p><b>Accounts: {accounts}</b></p>
        </>
    );
};

export default IkaCoin;
