import React, { useState, useEffect } from 'react';
import provider from '../services/provider';

function Transfer() {

    const [accounts, setAccounts] = useState([]);
    const [selectedAccount, setSelectedAccount] = useState('');
    const [amount, setAmount] = useState();

    useEffect(() => {
        provider.listAccounts().then(function (result) {
            console.log(result);
            setAccounts(result);
            selectedAccount = accounts[0];
        }).catch(function (error) {
            console.log(error);
        });
    }, []);

    function handleChange(value){
        setSelectedAccount(value);
    };

    function handleAmount(value){
        setAmount(value);
    };

    function handleSubmit(event){
        console.log("Request tranfer. To: "+selectedAccount+", Amount: "+amount);
    }

    return (
        <form onSubmit={handleSubmit}>
            <label>
                To Address: 
                <select  value={selectedAccount} onChange={handleChange}>
                    {accounts.map((account) => (
                        <option key={accounts.indexOf(account)} value={account}>{account}</option>
                    ))}
                </select>
            </label>
            <label>
                Amount: 
                <input type="number" value={amount} onChange={handleAmount}/>IKC
            </label>
            <input type="submit" value="Transfer" />
        </form>
    );
};

export default Transfer;
