import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import {TokenInstance, Init, onConnect} from "./services/EthereumConnexion";

function App() {
  const [name, setName] = useState("");
  const [symbol, setSymbol] = useState("");
  useEffect(() => {
    //console.log(tokenInstance._address);
    console.log("Initialization...");
    Init();
    onConnect().then(function (result) {
      //setName(result);
      const tokenInstance = TokenInstance();
      console.log(tokenInstance.address);
      tokenInstance.name().then(function(result){
        setName(result);
      }).catch(function(error){
        console.log(error);
      });
      tokenInstance.symbol().then(function(result){
        setSymbol(result);
      }).catch(function(error){
        console.log(error);
      });
    }).catch(function (error) {
      console.log(error);
    });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          {name}
        </p>
        <p>
          {symbol}
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
      <Button variant="danger">Primary</Button>{' '}
    </div>
  );
}

export default App;
