import provider from './provider';
import React, { useState, useEffect } from 'react';
import getInstance from './EthereumConnexion';

function IkaCoin() {

    const [name, setName] = useState("");
    const [symbol, setSymbol] = useState("");
    const [totalSupply, setTotalSupply] = useState(0);
    const [accounts, setAccounts] = useState([]);

    useEffect(() => {

        const tokenInstance = getInstance();

        tokenInstance.name().then(function (result) {
            console.log(result);
            setName(result);
        }).catch(function (error) {
            console.log(error);
        });

        tokenInstance.symbol().then(function(result){
            console.log(result);
            setSymbol(result);
        }).catch(function(error){
            console.log(error);
        });

        tokenInstance.totalSupply().then(function(result){
            console.log(result);
            setTotalSupply(result);
        }).catch(function(error){
            console.log(error);
        });

        provider.listAccounts().then(function(result){
            setAccounts(result);
            console.log(result);
        }).catch(function(error){
            console.log(error);
        });

    }, []);

    return (
        <>
            <p><b>{name}</b></p>
            <p>Total Supply: {totalSupply} {symbol}</p>
            <p><b>{accounts}</b></p>
        </>
    );
};

export default IkaCoin;
