import React, { useState, useEffect } from 'react';
import { ethers } from "ethers";
import provider from '../services/provider';
import EthereumConnexion from '../services/EthereumConnexion';

function Transfer() {

    const [accounts, setAccounts] = useState([]);
    const [selectedAccount, setSelectedAccount] = useState('');
    const [amount, setAmount] = useState(0);

    useEffect(() => {
        provider.listAccounts().then(function (result) {
            console.log(result);
            setAccounts(result);
            setSelectedAccount(accounts[0]);
        }).catch(function (error) {
            console.log(error);
        });
    }, []);

    function handleChange(event){
        setSelectedAccount(event.target.value);
    };

    function handleAmount(event){
        setAmount(event.target.value);
    };

    function handleSubmit(event){
        console.log("Request tranfer. To: "+accounts[selectedAccount]+", Amount: "+amount);

        //ethers.utils.bigNumberify(0)

        EthereumConnexion.CreateInstances();
        const insanceWithSigner = EthereumConnexion.GetInsanceWithSigner();

        const to = accounts[selectedAccount];
        //amount = amount * (10 ** 18); 

        console.log(amount);

        insanceWithSigner.transfer(to, ethers.utils.bigNumberify(amount))
                          .then(function(result){
                              console.log("Transfer succeded!");
                          }).catch(function(error){
                              console.log(error);
                          });
        window.alert('Transfert');

        event.preventDefault();
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                To Address: 
                <select  value={selectedAccount} onChange={handleChange}>
                    {accounts.map((account) => (
                        <option key={accounts.indexOf(account).toString()} value={accounts.indexOf(account)}>{account}</option>
                    ))}
                </select>
            </label>
            <label>
                Amount: 
                <input type="number" value={amount} onChange={handleAmount}/>IKC
            </label>
            <input type="submit" value="Transfer" />
        </form>
    );
};

export default Transfer;
