export function Init() {

  const providerOptions = {
    walletconnect: {
      package: WalletConnectProvider,
      options: {
        rpc: {
          1: "http://127.0.0.1:9545/"
        }
      }
    }
  };
  web3Modal = new Web3Modal({
    network: 'localhost',
    cacheProvider: true,
    providerOptions: providerOptions
  });

  console.log("Web3modal instance is: " + web3Modal);
};

export async function onConnect() {
  console.log("Opening a dialog...");

  try {

    provider = await web3Modal.connect();
    const providerEth = new ethers.providers.JsonRpcProvider();
    tokenInstance = new ethers.Contract(contractAddress, abi, providerEth);
    console.log(tokenInstance.methods);

  } catch (error) {
    console.log("Could not connect to wallet", error);
  }

  // Subscribe to accounts change
  provider.on("accountsChanged", (accounts) => {
    console.log(accounts);
    selectedAccount = accounts[0];
    console.log("Selected account is:"+selectedAccount);
  });

  // Subscribe to chainId change
  provider.on("chainChanged", (chainId) => {
    console.log(chainId);
  });

  // Subscribe to provider connection
  provider.on("connect", (info) => {
    console.log(info);
  });

  // Subscribe to provider disconnection
  provider.on("disconnect", (error) => {
    console.log(error);
  });
}

