import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import { TokenInstance, Init, onConnect, getAccounts } from "./services/EthereumConnexion";

function App() {

  const [name, setName] = useState("");
  const [symbol, setSymbol] = useState("");
  const [totalSupply, setTotalSupply] = useState(0);
  const [accounts, setAccounts] = useState([]);
  const [tokenInstance, setTokenInstance] = useState();

  useEffect(() => {
    console.log("Initialization...");
    Init();
    const token = TokenInstance();
    setTokenInstance(token);

    onConnect().then(function (result) {
      //setName(result);

      tokenInstance.name().then(function (result) {
        setName(result);
      }).catch(function (error) {
        console.log(error);
      });

      tokenInstance.symbol().then(function (result) {
        setSymbol(result);
      }).catch(function (error) {
        console.log(error);
      });

      tokenInstance.totalSupply().then(function (result) {
        let supp = parseFloat(result.toString()) / 10 ** 18;
        setTotalSupply(supp);
      }).catch(function (error) {
        console.log(error);
      });

      getAccounts().then(function (result) {
        setAccounts([...accounts, result]);
      }).catch(function (error) {
        console.log(error);
      });

    }).catch(function (error) {
      console.log(error);
    });
  }, []);

  const getCurrentBalance = () => {
    tokenInstance.balanceOf(accounts[0]).then(function(result){
      return result;
    }).catch(function(error){
      console.log(error);
    });
  };

  return (
    <>
      <p><b>{name}</b></p>
      <p>Total Supply: {totalSupply} {symbol}</p>
      <p>My balance: {getCurrentBalance}</p>
      {/* <Button variant="primary">Primary</Button>{' '} */}
    </>
  );
}

export default App;
