import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { TokenInstance, Init, onConnect } from "./services/EthereumConnexion";

function App() {
  const [name, setName] = useState("");
  const [symbol, setSymbol] = useState("");
  useEffect(() => {
    //console.log(tokenInstance._address);
    console.log("Initialization...");
    Init();
    onConnect().then(function (result) {
      //setName(result);
      const tokenInstance = TokenInstance();
      tokenInstance.name().then(function (result) {
        setName(result);
      }).catch(function (error) {
        console.log(error);
      });
      tokenInstance.symbol().then(function (result) {
        setSymbol(result);
      }).catch(function (error) {
        console.log(error);
      });
    }).catch(function (error) {
      console.log(error);
    });
  }, []);

  return (
    <>
      <div className="mb-2">
        <Button variant="primary" size="lg">
          Large button
    </Button>{' '}
        <Button variant="secondary" size="lg">
          Large button
    </Button>
      </div>
      <div>
        <Button variant="primary" size="sm">
          Small button
    </Button>{' '}
        <Button variant="secondary" size="sm">
          Small button
    </Button>
      </div>
    </>
  );
}

export default App;
